mapper.update:
update t_board 
   set hit_cnt = hit_cnt + 1
 where board_idx = #{boardIdx}

update t_board
   set title = #{title}, 
       contents = #{contents}, 
       updated_dt = now(), 
       updated_id = #{updatedId}
 where board_idx = #{boardIdx}

mapper.delete:
update t_board
   set deleted_yn = 'Y', 
       updated_dt = now(), 
       updated_id = #{updatedId}
 where board_idx = #{boardIdx}
